export const el=e=>document.querySelector(e);export const group=e=>document.querySelectorAll(e);export const create=e=>document.createElement(e);export async function loadJson(e){try{const o=await fetch(e);return o.ok?await o.json():(console.error(`Failed to load JSON from ${e}: ${o.statusText}`),null)}catch(e){return console.error("Network or JSON parsing error:",e),null}}