import{el,group,create}from"./lib.js";import{db}from"./db.js";export function getRespNavbar(){const e=Array.prototype.slice.call(group(".navbar-burger"),0);e.forEach(e=>{e.addEventListener("click",()=>{const t=e.dataset.target,a=document.getElementById(t);e.classList.toggle("is-active"),a.classList.toggle("is-active")})})}export async function getListButton(){const e=await db.readKeys(),t=el("#list-button"),a=el("#like-page");if(e.length>0){if(!a){const e=create("a");e.className="button is-link",e.id="like-page",e.href="./bookmarks.html",e.innerHTML="My List",t.append(e)}}else a&&a.remove()}